package org.ops4j.base.io;import java.io.IOException;import java.io.InputStream;import org.ops4j.store.Handle;import org.ops4j.store.Store;/** *  */public class EagerCachedInputStreamSource implements InputStreamSource{    private final Store<InputStream> m_store;    private final Handle m_handle;    public EagerCachedInputStreamSource( Store<InputStream> store, InputStream inp )        throws IOException    {        m_store = store;        m_handle = store.store( inp );    }    public InputStream get()        throws IOException    {        return m_store.load( m_handle );    }}