package org.ops4j.base.io;import java.io.IOException;import java.io.InputStream;import org.ops4j.store.Handle;import org.ops4j.store.Store;/** * */public class LazyCachedInputStreamSource implements InputStreamSource{    private final Store<InputStream> m_store;    private Handle m_handle;    private InputStream m_inp;    public LazyCachedInputStreamSource( Store<InputStream> store, InputStream inp )        throws IOException    {        m_store = store;        m_inp = inp;    }    public synchronized InputStream get()        throws IOException    {        if( m_handle == null )        {            m_handle = m_store.store( m_inp );        }                return m_store.load( m_handle );    }}