package org.ops4j.repository.base;import java.io.IOException;import java.io.InputStream;import org.junit.Test;import org.ops4j.pax.repository.base.CachedArtifact;import org.ops4j.store.Store;import static org.mockito.Mockito.*;/** * */public class CachedArtifactTest{    @Test    public void test()        throws IOException    {        Store<InputStream> store = mock( Store.class );        InputStream input = getClass().getResourceAsStream( "/test.txt" );        new CachedArtifact( store, input );        verify( store, times( 1 ) ).store( input );    }}