package org.ops4j.repository.base;import java.io.IOException;import org.junit.Test;import org.ops4j.pax.repository.base.EmptyFilter;import static org.junit.Assert.*;/** * Empty Filters Test */public class EmptyFilterTest{    @Test    public void testDefaultTrue()        throws IOException    {        EmptyFilter<String> filter = new EmptyFilter<String>( true );        assertTrue( filter.allow( "boo" ) );        assertTrue( filter.allow( "" ) );        assertTrue( filter.allow( null ) );    }    @Test    public void testDefaultFalse()        throws IOException    {        EmptyFilter<String> filter = new EmptyFilter<String>( false );        assertFalse( filter.allow( "boo" ) );        assertFalse( filter.allow( "" ) );        assertFalse( filter.allow( null ) );    }}